// 1. float reset
// -------------------------------------------------------------
.clr {
  content: " ";
  clear: both;
  overflow: hidden;
  width: 1px;
  height: 1px;
  margin: 0 -1px -1px 0;
  border: 0;
  padding: 0;
  font-size: 0;
  line-height: 0;
}

// 2. float
// -------------------------------------------------------------

.f-left {
  @if $support-ie7 {
    display: inline;
  }
  float: left;
}

.f-right {
  @if $support-ie7 {
    display: inline;
  }
  float: right;
}

// 3. align
// -------------------------------------------------------------

.t-left { text-align: left !important;}
.t-center { text-align: center !important;}
.t-right { text-align: right !important;}
.v-top { vertical-align: top !important;}
.v-middle { vertical-align: middle !important;}
.v-bottom { vertical-align: bottom !important;}

table.t-left,
thead.t-left tr,
tbody.t-left tr,
tr.t-left {
  th, td {
    text-align: left !important;
    &.t-left { text-align: left !important;}
    &.t-center { text-align: center !important;}
    &.t-right { text-align: right !important;}
  }
}
table.t-center,
thead.t-center tr,
tbody.t-center tr,
tr.t-center {
  th, td {
    text-align: center !important;
    &.t-left { text-align: left !important;}
    &.t-center { text-align: center !important;}
    &.t-right { text-align: right !important;}
  }
}
table.t-right,
thead.t-right tr,
tbody.t-right tr,
tr.t-right {
  th, td {
    text-align: right !important;
    &.t-left { text-align: left !important;}
    &.t-center { text-align: center !important;}
    &.t-right { text-align: right !important;}
  }
}

table.v-top,
thead.v-top tr,
tbody.v-top tr,
tr.v-top {
  th, td {
    vertical-align: top !important;
    &.v-top { vertical-align: top !important;}
    &.v-middle { vertical-align: middle !important;}
    &.v-bottom { vertical-align: bottom !important;}
  }
}
table.v-middle,
thead.v-middle tr,
tbody.v-middle tr,
tr.v-middle {
  th, td {
    vertical-align: middle !important;
    &.v-top { vertical-align: top !important;}
    &.v-middle { vertical-align: middle !important;}
    &.v-bottom { vertical-align: bottom !important;}
  }
}
table.v-bottom,
