// 8. show hide
// -------------------------------------------------------------

.show {
  display: block!important;
}
.hide {
  display: none!important;
}
.visible {
  max-height: 99999px;
  visibility: visible;
  overflow: visible;
  @if $support-ie8 {
    filter: progid:DXImageTransform.Microsoft.Alpha(Opacity=100);
  }
  opacity: 1;
}
.hidden {
  max-height: 0;
  visibility: hidden;
  overflow: hidden;
  @if $support-ie8 {
    filter: progid:DXImageTransform.Microsoft.Alpha(Opacity=0);
  }
  opacity: 0;
}

// setting
@import "setting";

// functions
@import "method/font";
@import "method/util";

// mixin
@import "mixin/mixin";

// dependencies mixin / function
@import "dependencies/functions";

@if( $support-ie8 ) {
  // fluid-img
  @mixin fluid_img($w: null) {
    width: #{$w};
    max-width: 100%;
    height: auto;
    width: auto\9;
  }

  // http://compass-style.org/reference/compass/css3/opacity/
  @mixin opacity ( $opacity ) {
    @if $opacity == 1 {
      filter: unquote("progid:DXImageTransform.Microsoft.Alpha(enabled=false)");
    }
    @else {
      filter: unquote("progid:DXImageTransform.Microsoft.Alpha(Opacity=#{round($opacity * 100)})");
    }

    opacity: $opacity;
  }
}

@if( $support-ie7 ) {
  // inline-block
  @mixin inline-block {
    display: inline-block;
    *display: inline;
    *zoom: 1;
  }

  // table-cell
  @mixin table-cell ($vertical: middle) {
    display: table-cell;
    vertical-align: $vertical;
    *display: inline;
    *zoom: 1;
  }

  // clearfix
  @mixin clearfix {
    &:before,
    &:after { content:""; display:table;}
    &:after { clear:both;}
    & { zoom:1;}
  }

  // fluid-img
  @mixin fluid_img($w: null) {
    width: #{$w};
    max-width: 100%;
    height: auto;
    *width:100%;
  }

  // font smoothing
  @mixin font-smoothing($value: on) {
    @if $value == on {
      -webkit-font-smoothing: antialiased;
      -moz-osx-font-smoothing: grayscale;
    }
    @else {
      -webkit-font-smoothing: subpixel-antialiased;
      -moz-osx-font-smoothing: auto;
    }
  }

  // hide-text
  @mixin hide-text {
    overflow: hidden;
    font-size: 0;
    text-indent: 100%;
    white-space: nowrap;
    outline: none;
  }
}

